import './App.css';
import MyCard from './MyCard';
import load from './Animation - 175171Loading7760308.json';
import { useEffect, useState } from 'react';
import { DeleteStd, GetData } from './API';
import Lottie from 'lottie-react';

function App() {
  const [apiData, setApiData] = useState([]); // ✅ Fix: useState([]) not ['']
  const [Loading, setLoading] = useState(true); // ✅ Correct useState()

  const Apidata = async () => {
    setLoading(true);
    try {
      const d = await GetData();
      setApiData(Array.isArray(d.data) ? d.data : []);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const deleteData = async (id) => {
    try {
      const res = await DeleteStd(id);
      if (res.status === 200) {
        alert('deleted!');
        Apidata(); // refresh list
      } else {
        alert('id not found');
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    Apidata();
  }, []);

  return (
    <>
      {Loading ? (
        <div className="loading-container">
          <Lottie animationData={load} loop={true} style={{ width: 150, height: 150 }} />
          <p>Loading...</p>
        </div>
      ) : (
        <MyCard data={apiData} dlt={deleteData} />
      )}
    </>
  );
}

export default App;
