// import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom';
import './Update.css' ;
import { Form , Row , Col , Button } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { StdbyID } from './API';



export default function Update({dt}) {
    
    const [data , setData] = useState(['']) ;

    const ApiD = async () => {
    let id = useLocation().pathname.split('/').at(2)
        try {
            const d = await StdbyID(id) ;
            if (d.status === 200) {
                setData(d.data) ;
            }
            else {console.log(d.status)}
 
      }
        catch (error) {
            console.log(error)
        }
    }

    useEffect (()=> {
        ApiD() ;
    },
[])



  return (
    <>  {console.log(data)}
        <div className='first'>
            <h3> Update Student Info</h3>
            <Form className='form'>
                <Row>
                    {/* <Form.Control value={'ID'} className='id'/> */}
                </Row>
                <Row> 
                    <Col>
                        <Form.Control placeholder="First name"  className='textBox' required/>
                    </Col>
                    <Col>
                        <Form.Control placeholder="Last name" className='textBox' required/>
                    </Col>
                </Row>
                <Row> 
                    <Col>
                        <Form.Control placeholder="Phone number"  className='textBox' required/>
                    </Col>
                    <Col>
                        <Form.Control placeholder="Email" className='textBox' required />
                    </Col>
                </Row>
                
                <Row>
                    <Button className='button'>
                               Update Data
                     </Button>
                </Row>


            </Form>
        </div>
    </>
  )
}
