import './App.css';
import MyCard from './MyCard';
import load from './Animation - 175171Loading7760308.json';
import { useEffect, useState } from 'react';
import { DeleteStd, GetData } from './API';
import Lottie from 'lottie-react';

function App() {
  const [apiData, setApiData] = useState([]);
  const [Loading, setLoading] = useState(true);

  const Apidata = async () => {
    setLoading(true);
    try {
      const d = await GetData();
      setTimeout(() => {
        setApiData(d.data);
        setLoading(false);
      }, 2000);
    } catch (error) {
      console.error(error);
    }
  };

  const deleteData = async (id) => {
    try {
      const res = await DeleteStd(id);
      if (res.status === 200) {
        alert('Deleted!');
        Apidata();
      } else {
        alert('ID not found');
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    Apidata();
  }, []);

  return (
    <>
      {Loading ? (
        <Lottie
          animationData={load}
          loop={true}
          style={{ height: '500px', width: '500px' }}
        />
      ) : (
        <MyCard data={apiData} dlt={deleteData} />
      )}
    </>
  );
}

export default App;
