// import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom';
import './Update.css' ;
import { Form , Row , Col , Button } from 'react-bootstrap';
import { useEffect, useState } from 'react';
import { StdbyID } from './API';



export default function Update() {
    const [data , setData] = useState({
        'id' : '' , 
        'email' : '' ,
        'name' : '' , 
        'phone' : ''
    })
    const [email , setEmail] = useState() ;
    const [ID , setID] = useState() ;
    const [name , setName] = useState() ;
    const [phone , setPhone] = useState() ;    

    let id = useLocation().pathname.split('/').at(2)
    const ApiD = async () => {
    
        try {
            const d = await StdbyID(id) ;
            if (d.status === 200) {
                setEmail(d.data.email) ;
                setID(d.data.id) ;
                setName(d.data.name) ;
                setPhone(d.data.phone) ;
                console.log(d.data)
            }
            else {console.log(d.status)}
 
      }
        catch (error) {
            console.log(error)
        }

        
    }
     useEffect (()=> {
        ApiD () ;
    },
[])


const DataIn = (event) => {

    let key = event.target.name ;
    let value = event.target.value ;

}

  return (
    <>  
        <div className='first'>
            <h3> Update Student Info</h3>
            <Form className='form'>
                
                <Row> 
                    <Col>
                          <Form.Control value={ID} className='textBox'/>
                    </Col>
                    <Col>
                        <Form.Control onChange={(event)=>setName(event.target.value)}  value={name} placeholder="Name"  className='textBox' name='name' required/>
                    </Col>
                    
                </Row>
                <Row> 
                    <Col>
                        <Form.Control onChange={(event)=>setPhone(event.target.value)} value={phone} placeholder="Phone number" type='number' name='phone'  className='textBox' required/>
                    </Col>
                    <Col>
                        <Form.Control onChange={(event)=>setEmail(event.target.value)} value = {email} placeholder="Email" type='email' className='textBox' required />
                    </Col>
                </Row>
                
                <Row>
                    <Button className='button'>
                               Update Data
                     </Button>
                </Row>


            </Form>
        </div>
    </>
  )
}
