import location from './location-pin-svgrepo-com.svg' ;
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';
import { useState } from 'react';
import axios from 'axios';

function App() {

  let [formData, setFormData] = useState('') ;
  let [apiData, setAPIData] = useState () ;
  let [showCard, setshowCard] = useState(false) ;



  let OnSubmit=async(event) => {
    event.preventDefault();
    try {
      let cName = formData ;
      let res = await axios.get(`https://api.weatherapi.com/v1/current.json?key=4a337b7a3a3549a785d174314250107&q=${cName}&aqi=n`)
      if (res.status===200) 
        { setshowCard(true) ;
          setAPIData(res.data) ;
        }        
    }

    catch (e) {
      // console.log(e)
      toast.error("City not found ! ")
          setshowCard(false) ;
          setAPIData([]) ;
    }


    // let cName = formData ;
    // fetch(`https://api.weatherapi.com/v1/current.json?key=4a337b7a3a3549a785d174314250107&q=${cName}&aqi=n`)
    // .then(res => {
    //   if (!res.ok) {throw new Error ()}
    //     else { return res.json()}

    // } )
    // .then(FinalResponse => {
    //   setAPIData([FinalResponse])
    //   setshowCard(true) ; 
    // })

    //  .catch(() => {toast.error('City not found ! ')
    //   setshowCard(false)
    //   setAPIData([])


    //  })


  }
      // useEffect(()=>{} , [OnSubmit])



  return (

   <> {console.log(apiData)}
    <ToastContainer/>
      <div className='main'>
        <h1>Know your city weather</h1>
        <form onSubmit={OnSubmit}>
          <label>Enter your city </label> <br/>  <img src={location} alt ='location'/>
          <input type='text' value={formData} name='cName' onChange={(event)=>setFormData(event.target.value)}/>  <span/> <br/> 
          <button>Get Data</button>
        </form>
        </div>

        <div className= {(showCard) ? "data" : ""}>
          {/* {apiData.map((v, i)=> {return ( */}
            <>
            <div className='time'>
            <p>Last updated on : {apiData.current.last_updated} </p>
          </div>
          <div className='text'>
            <img src={location} alt ='location'/>
            <h1>{apiData.location.name}</h1> 
            <p>{apiData.location.region}</p> 
            <p>{apiData.location.country}</p> 

          </div>

          <p>Local Time : {apiData.location.localtime}</p>

          <div className='temp'>
            <p>{apiData.current.temp_c}<sup>°C</sup></p>

          </div>
          {/* <img src = {`https://cdn.weatherapi.com/weather/64x64/day/${v.current.condition.icon}.png`} alt = 'weather' /> */}

          <img src = {`https:${apiData.current.condition.icon}`} alt = 'weather' />
          <figcaption>{apiData.current.condition.text}</figcaption>
          <button className='ai'>📄➡️📋Summarize with AI</button>



          </>
          {/* )})} */}



        </div>
        <p className='co'>© Raj Shantaram Parsharam</p>

   </>
  );
}

export default App;
