import location from './location-pin-svgrepo-com.svg' ;
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';
import { useState, useEffect } from 'react';

function App() {

  let [formData, setFormData] = useState('') ;
  let [apiData, setAPIData] = useState ([]) ;



  let OnSubmit=(event) => {
    event.preventDefault();
    let cName = formData ;
    fetch(`http://api.weatherapi.com/v1/current.json?key=4a337b7a3a3549a785d174314250107&q=${cName}&aqi=n`)
    .then(res => {
      if (!res.ok) {throw new Error ()}
        else { return res.json()}

    } )
    .then(FinalResponse => {
      setAPIData([FinalResponse]) 
    })

     .catch(() => {toast.error('City not found ! ')


     })



  }
      useEffect(()=>{} , [OnSubmit])



  return (
   <> <ToastContainer/>
      <div className='main'>
        <h1>Know your city weather</h1>
        <form onSubmit={OnSubmit}>
          <label>Enter your city </label> <br/>  <img src={location} alt ='location'/>
          <input type='text' value={formData} name='cName' onChange={(event)=>setFormData(event.target.value)}/>  <span/> <br/> 
          <button>Get Data</button>
        </form>
        </div>

        <div className='data'>
          {apiData.map((v, i)=> {return (<>
            <div className='time'>
            <p>Last updated on : {v.current.last_updated} </p>
          </div>
          <div className='text'>
            <img src={location} alt ='location'/>
            <h1>{v.location.name}</h1> 
            <p>{v.location.region}</p> 
            <p>{v.location.country}</p> 

          </div>

          <p>Local Time : {v.location.localtime}</p>

          <div className='temp'>
            <p>{v.current.temp_c}<sup>°C</sup></p>

          </div>
          {/* <img src = {`https://cdn.weatherapi.com/weather/64x64/day/${v.current.condition.icon}.png`} alt = 'weather' /> */}

          <img src = {`https:${v.current.condition.icon}`} alt = 'weather' />
          <figcaption>{v.current.condition.text}</figcaption>









          </>)})}



        </div>
        <p className='co'>© Raj Shantaram Parsharam</p>

   </>
  );
}

export default App;
